import React, { useState, useEffect, useCallback, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Play, 
  Square, 
  CheckCircle, 
  XCircle, 
  Clock, 
  Zap, 
  Activity,
  Target,
  TrendingUp,
  Wifi,
  WifiOff,
  RefreshCw,
  Settings,
  BarChart3,
  TestTube,
  Timer,
  AlertCircle
} from 'lucide-react';

// Types
interface TestResult {
  id: string;
  name: string;
  type: 'unit' | 'integration' | 'e2e' | 'performance' | 'security';
  status: 'pending' | 'running' | 'passed' | 'failed' | 'skipped' | 'cancelled';
  duration: number;
  message: string;
  progress: number;
  timestamp: number;
}

interface TestSuite {
  id: string;
  name: string;
  description: string;
  status: 'pending' | 'running' | 'passed' | 'failed' | 'skipped' | 'cancelled';
  progress: number;
  duration: number;
  passed_count: number;
  failed_count: number;
  total_count: number;
  tests: TestResult[];
}

interface TestState {
  run_id: string | null;
  running: boolean;
  suites: Record<string, TestSuite>;
  timestamp: number;
}

const RealtimeTestingPanel: React.FC = () => {
  const [testState, setTestState] = useState<TestState>({
    run_id: null,
    running: false,
    suites: {},
    timestamp: 0
  });

  const [connectionStatus, setConnectionStatus] = useState<'connecting' | 'connected' | 'disconnected'>('disconnected');
  const [selectedTypes, setSelectedTypes] = useState<string[]>(['integration']);
  const [logs, setLogs] = useState<Array<{type: string, message: string, timestamp: number}>>([]);
  const [isExpanded, setIsExpanded] = useState(false);

  const wsRef = useRef<WebSocket | null>(null);
  const reconnectTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  // WebSocket connection management
  const connectWebSocket = useCallback(() => {
    if (wsRef.current?.readyState === WebSocket.OPEN) return;

    setConnectionStatus('connecting');
    wsRef.current = new WebSocket('ws://localhost:8765');

    wsRef.current.onopen = () => {
      setConnectionStatus('connected');
      addLog('info', 'Connected to test server');
    };

    wsRef.current.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        handleWebSocketMessage(data);
      } catch (error) {
        console.error('Error parsing WebSocket message:', error);
      }
    };

    wsRef.current.onclose = () => {
      setConnectionStatus('disconnected');
      addLog('warning', 'Disconnected from test server');

      // Auto-reconnect after 3 seconds
      reconnectTimeoutRef.current = setTimeout(() => {
        connectWebSocket();
      }, 3000);
    };

    wsRef.current.onerror = (error) => {
      console.error('WebSocket error:', error);
      addLog('error', 'WebSocket connection error');
    };
  }, []);

  const handleWebSocketMessage = (data: any) => {
    switch (data.type) {
      case 'test_state':
        setTestState(data);
        break;

      case 'test_run_started':
        addLog('info', `Test run started: ${data.run_id}`);
        break;

      case 'test_run_completed':
        addLog('success', `Test run completed`);
        break;

      case 'suite_started':
        addLog('info', `Suite started: ${data.suite_name}`);
        break;

      case 'test_completed':
        const status = data.status === 'passed' ? 'success' : 'error';
        addLog(status, `${data.test_name}: ${data.status}`);
        break;

      default:
        break;
    }
  };

  const addLog = (type: string, message: string) => {
    setLogs(prev => [...prev.slice(-19), { type, message, timestamp: Date.now() }]);
  };

  const sendMessage = (message: any) => {
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify(message));
    }
  };

  const startTests = () => {
    sendMessage({
      type: 'run_tests',
      testTypes: selectedTypes
    });
  };

  const stopTests = () => {
    sendMessage({
      type: 'stop_tests'
    });
  };

  const toggleTestType = (type: string) => {
    setSelectedTypes(prev => 
      prev.includes(type) 
        ? prev.filter(t => t !== type)
        : [...prev, type]
    );
  };

  useEffect(() => {
    connectWebSocket();

    return () => {
      if (reconnectTimeoutRef.current) {
        clearTimeout(reconnectTimeoutRef.current);
      }
      wsRef.current?.close();
    };
  }, [connectWebSocket]);

  // Helper functions
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'passed': return 'text-green-400';
      case 'failed': return 'text-red-400';
      case 'running': return 'text-blue-400';
      case 'pending': return 'text-gray-400';
      case 'skipped': return 'text-yellow-400';
      default: return 'text-gray-400';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'passed': return <CheckCircle className="w-3 h-3" />;
      case 'failed': return <XCircle className="w-3 h-3" />;
      case 'running': return <RefreshCw className="w-3 h-3 animate-spin" />;
      case 'pending': return <Clock className="w-3 h-3" />;
      default: return <Clock className="w-3 h-3" />;
    }
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'unit': return 'bg-blue-500/20 text-blue-300';
      case 'integration': return 'bg-purple-500/20 text-purple-300';
      case 'e2e': return 'bg-green-500/20 text-green-300';
      case 'performance': return 'bg-orange-500/20 text-orange-300';
      case 'security': return 'bg-red-500/20 text-red-300';
      default: return 'bg-gray-500/20 text-gray-300';
    }
  };

  const totalTests = Object.values(testState.suites).reduce((sum, suite) => sum + suite.total_count, 0);
  const passedTests = Object.values(testState.suites).reduce((sum, suite) => sum + suite.passed_count, 0);
  const failedTests = Object.values(testState.suites).reduce((sum, suite) => sum + suite.failed_count, 0);
  const overallProgress = totalTests > 0 ? ((passedTests + failedTests) / totalTests) * 100 : 0;

  // Compact view for main dashboard
  if (!isExpanded) {
    return (
      <div className="glass-dark p-6 rounded-xl border border-white/10">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-3">
            <TestTube className="w-5 h-5 text-blue-400" />
            <h3 className="font-semibold text-white">Real-time Testing</h3>
            <div className="flex items-center space-x-2">
              {connectionStatus === 'connected' ? (
                <Wifi className="w-4 h-4 text-green-400" />
              ) : (
                <WifiOff className="w-4 h-4 text-red-400" />
              )}
              <span className={`text-xs ${
                connectionStatus === 'connected' ? 'text-green-400' : 'text-red-400'
              }`}>
                {connectionStatus}
              </span>
            </div>
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => setIsExpanded(true)}
            className="text-blue-400 hover:text-blue-300 transition-colors"
          >
            <BarChart3 className="w-5 h-5" />
          </motion.button>
        </div>

        {/* Compact Stats */}
        {totalTests > 0 ? (
          <div className="grid grid-cols-3 gap-4 mb-4">
            <div className="text-center">
              <p className="text-2xl font-bold text-white">{totalTests}</p>
              <p className="text-xs text-gray-400">Total</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-green-400">{passedTests}</p>
              <p className="text-xs text-gray-400">Passed</p>
            </div>
            <div className="text-center">
              <p className="text-2xl font-bold text-red-400">{failedTests}</p>
              <p className="text-xs text-gray-400">Failed</p>
            </div>
          </div>
        ) : (
          <div className="text-center py-4">
            <p className="text-gray-400 text-sm">No tests running</p>
          </div>
        )}

        {/* Progress Bar */}
        {totalTests > 0 && (
          <div className="mb-4">
            <div className="w-full bg-gray-700 rounded-full h-2">
              <motion.div
                className="bg-gradient-to-r from-blue-500 to-green-500 h-2 rounded-full"
                initial={{ width: 0 }}
                animate={{ width: `${overallProgress}%` }}
                transition={{ duration: 0.5 }}
              />
            </div>
          </div>
        )}

        {/* Control Buttons */}
        <div className="flex space-x-2">
          {!testState.running ? (
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={startTests}
              disabled={connectionStatus !== 'connected'}
              className="flex-1 flex items-center justify-center space-x-2 bg-gradient-to-r from-green-500 to-blue-500 px-4 py-2 rounded-lg font-semibold text-white disabled:opacity-50"
            >
              <Play className="w-4 h-4" />
              <span>Start</span>
            </motion.button>
          ) : (
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={stopTests}
              className="flex-1 flex items-center justify-center space-x-2 bg-gradient-to-r from-red-500 to-orange-500 px-4 py-2 rounded-lg font-semibold text-white"
            >
              <Square className="w-4 h-4" />
              <span>Stop</span>
            </motion.button>
          )}

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => setIsExpanded(true)}
            className="px-4 py-2 bg-gray-700 text-white rounded-lg"
          >
            <Settings className="w-4 h-4" />
          </motion.button>
        </div>

        {/* Recent Logs */}
        {logs.length > 0 && (
          <div className="mt-4 space-y-1">
            <p className="text-xs text-gray-400 mb-2">Recent Activity:</p>
            {logs.slice(-3).map((log, index) => (
              <div key={index} className="flex items-center space-x-2">
                <div className={`w-1 h-1 rounded-full ${
                  log.type === 'success' ? 'bg-green-400' :
                  log.type === 'error' ? 'bg-red-400' :
                  log.type === 'warning' ? 'bg-yellow-400' :
                  'bg-blue-400'
                }`} />
                <p className="text-xs text-gray-300 truncate">{log.message}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    );
  }

  // Expanded view
  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4"
    >
      <motion.div
        initial={{ y: 50 }}
        animate={{ y: 0 }}
        className="bg-gray-900 rounded-xl border border-gray-700 max-w-6xl w-full max-h-[90vh] overflow-hidden"
      >
        {/* Header */}
        <div className="flex justify-between items-center p-6 border-b border-gray-700">
          <div className="flex items-center space-x-4">
            <TestTube className="w-6 h-6 text-blue-400" />
            <h2 className="text-xl font-bold text-white">Real-time Testing Dashboard</h2>
            <div className="flex items-center space-x-2">
              {connectionStatus === 'connected' ? (
                <Wifi className="w-4 h-4 text-green-400" />
              ) : (
                <WifiOff className="w-4 h-4 text-red-400" />
              )}
              <span className={`text-sm ${
                connectionStatus === 'connected' ? 'text-green-400' : 'text-red-400'
              }`}>
                {connectionStatus.charAt(0).toUpperCase() + connectionStatus.slice(1)}
              </span>
            </div>
          </div>

          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => setIsExpanded(false)}
            className="text-gray-400 hover:text-white transition-colors"
          >
            <XCircle className="w-6 h-6" />
          </motion.button>
        </div>

        <div className="p-6 max-h-[80vh] overflow-y-auto">
          {/* Test Type Selection */}
          <div className="mb-6">
            <h3 className="text-lg font-semibold text-white mb-3">Test Configuration</h3>
            <div className="flex flex-wrap gap-2">
              {['integration', 'unit', 'e2e', 'performance', 'security'].map(type => (
                <motion.button
                  key={type}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => toggleTestType(type)}
                  className={`px-3 py-1 rounded-lg font-medium transition-all text-sm ${
                    selectedTypes.includes(type)
                      ? 'bg-blue-500 text-white'
                      : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
                  }`}
                >
                  {type.charAt(0).toUpperCase() + type.slice(1)}
                </motion.button>
              ))}
            </div>
          </div>

          {/* Controls */}
          <div className="flex space-x-4 mb-6">
            {!testState.running ? (
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={startTests}
                disabled={connectionStatus !== 'connected'}
                className="flex items-center space-x-2 bg-gradient-to-r from-green-500 to-blue-500 px-6 py-3 rounded-lg font-semibold text-white disabled:opacity-50"
              >
                <Play className="w-5 h-5" />
                <span>Start Tests</span>
              </motion.button>
            ) : (
              <motion.button
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={stopTests}
                className="flex items-center space-x-2 bg-gradient-to-r from-red-500 to-orange-500 px-6 py-3 rounded-lg font-semibold text-white"
              >
                <Square className="w-5 h-5" />
                <span>Stop Tests</span>
              </motion.button>
            )}
          </div>

          {/* Stats Overview */}
          {totalTests > 0 && (
            <div className="grid grid-cols-4 gap-4 mb-6">
              <div className="bg-gray-800/50 p-4 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-400 text-sm">Total Tests</p>
                    <p className="text-xl font-bold text-white">{totalTests}</p>
                  </div>
                  <Target className="w-6 h-6 text-blue-400" />
                </div>
              </div>

              <div className="bg-gray-800/50 p-4 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-400 text-sm">Passed</p>
                    <p className="text-xl font-bold text-green-400">{passedTests}</p>
                  </div>
                  <CheckCircle className="w-6 h-6 text-green-400" />
                </div>
              </div>

              <div className="bg-gray-800/50 p-4 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-400 text-sm">Failed</p>
                    <p className="text-xl font-bold text-red-400">{failedTests}</p>
                  </div>
                  <XCircle className="w-6 h-6 text-red-400" />
                </div>
              </div>

              <div className="bg-gray-800/50 p-4 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-gray-400 text-sm">Success Rate</p>
                    <p className="text-xl font-bold text-blue-400">
                      {totalTests > 0 ? Math.round((passedTests / totalTests) * 100) : 0}%
                    </p>
                  </div>
                  <TrendingUp className="w-6 h-6 text-blue-400" />
                </div>
              </div>
            </div>
          )}

          {/* Test Suites */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <div>
              <h3 className="text-lg font-semibold text-white mb-4">Test Suites</h3>

              <div className="space-y-4">
                {Object.values(testState.suites).map((suite, index) => (
                  <motion.div
                    key={suite.id}
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.1 }}
                    className="bg-gray-800/50 p-4 rounded-lg"
                  >
                    <div className="flex items-center justify-between mb-3">
                      <div className="flex items-center space-x-2">
                        <div className={getStatusColor(suite.status)}>
                          {getStatusIcon(suite.status)}
                        </div>
                        <h4 className="font-semibold text-white">{suite.name}</h4>
                      </div>
                      <span className="text-sm text-gray-400">
                        {suite.passed_count}/{suite.total_count}
                      </span>
                    </div>

                    <div className="w-full bg-gray-700 rounded-full h-2 mb-3">
                      <motion.div
                        className={`h-2 rounded-full ${
                          suite.status === 'passed' ? 'bg-green-500' :
                          suite.status === 'failed' ? 'bg-red-500' :
                          'bg-blue-500'
                        }`}
                        initial={{ width: 0 }}
                        animate={{ width: `${suite.progress}%` }}
                        transition={{ duration: 0.5 }}
                      />
                    </div>

                    <div className="space-y-2">
                      {suite.tests.slice(0, 3).map(test => (
                        <div key={test.id} className="flex items-center justify-between text-sm">
                          <div className="flex items-center space-x-2">
                            <div className={getStatusColor(test.status)}>
                              {getStatusIcon(test.status)}
                            </div>
                            <span className="text-gray-300">{test.name}</span>
                          </div>
                          <span className={`px-2 py-1 rounded-full text-xs ${getTypeColor(test.type)}`}>
                            {test.type}
                          </span>
                        </div>
                      ))}
                      {suite.tests.length > 3 && (
                        <p className="text-xs text-gray-400">
                          +{suite.tests.length - 3} more tests
                        </p>
                      )}
                    </div>
                  </motion.div>
                ))}
              </div>
            </div>

            {/* Live Activity */}
            <div>
              <h3 className="text-lg font-semibold text-white mb-4">Live Activity</h3>

              <div className="bg-gray-800/50 p-4 rounded-lg h-96 overflow-y-auto">
                <div className="space-y-2">
                  {logs.length === 0 ? (
                    <p className="text-gray-400 text-sm">No activity yet...</p>
                  ) : (
                    logs.slice(-20).map((log, index) => (
                      <motion.div
                        key={`${log.timestamp}-${index}`}
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        className="flex items-start space-x-2 p-2 rounded bg-gray-700/30"
                      >
                        <div className={`w-2 h-2 rounded-full mt-1 flex-shrink-0 ${
                          log.type === 'success' ? 'bg-green-400' :
                          log.type === 'error' ? 'bg-red-400' :
                          log.type === 'warning' ? 'bg-yellow-400' :
                          'bg-blue-400'
                        }`} />
                        <div className="flex-1 min-w-0">
                          <p className="text-sm text-gray-300 break-words">{log.message}</p>
                          <p className="text-xs text-gray-500">
                            {new Date(log.timestamp).toLocaleTimeString()}
                          </p>
                        </div>
                      </motion.div>
                    ))
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </motion.div>
  );
};

export default RealtimeTestingPanel;
