import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { SystemOverview } from './components/SystemOverview';
import { ActivityFeed } from './components/ActivityFeed';
import { ProjectSelector } from './components/ProjectSelector';
import { AIFeatures } from './components/AIFeatures';
import { useRealTimeData } from './hooks/useRealTimeData';

function App() {
  const [currentProjectId, setCurrentProjectId] = useState<number>(70835889);
  const { health, events, project, loading, error } = useRealTimeData(currentProjectId);

  const handleProjectChange = (newProjectId: number) => {
    setCurrentProjectId(newProjectId);
  };

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 flex items-center justify-center">
        <motion.div
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          className="glass-effect rounded-xl p-8 max-w-md text-center"
        >
          <div className="w-16 h-16 bg-error-100 rounded-full flex items-center justify-center mx-auto mb-4">
            <svg className="w-8 h-8 text-error-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
            </svg>
          </div>
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Connection Error</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button
            onClick={() => window.location.reload()}
            className="px-4 py-2 bg-primary-600 text-white rounded-lg hover:bg-primary-700 transition-colors"
          >
            Retry Connection
          </button>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-blue-50 to-indigo-50">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-30">
        <div className="absolute inset-0" style={{
          backgroundImage: `radial-gradient(circle at 25px 25px, rgba(59, 130, 246, 0.1) 2px, transparent 0)`,
          backgroundSize: '50px 50px'
        }} />
      </div>

      {/* Main Content */}
      <div className="relative z-10 min-h-screen">
        {/* Header Bar */}
        <motion.header
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          className="glass-effect border-b border-white/20 sticky top-0 z-20"
        >
          <div className="max-w-7xl mx-auto px-6 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="w-8 h-8 bg-gradient-to-r from-primary-600 to-primary-400 rounded-lg flex items-center justify-center">
                  <svg className="w-5 h-5 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                  </svg>
                </div>
                <div>
                  <h1 className="text-xl font-bold gradient-text">GitAIOps</h1>
                  <p className="text-xs text-gray-500">Real-time Dashboard</p>
                </div>
              </div>

              <div className="flex items-center gap-4">
                <div className="flex items-center gap-2 text-sm">
                  <div className={`w-2 h-2 rounded-full animate-pulse ${
                    health?.status === 'healthy' ? 'bg-success-500' : 'bg-error-500'
                  }`} />
                  <span className="text-gray-600">
                    {loading ? 'Connecting...' : 'Live'}
                  </span>
                </div>

                <div className="text-xs text-gray-500">
                  Project #{currentProjectId} • Last update: {new Date().toLocaleTimeString()}
                </div>
              </div>
            </div>
          </div>
        </motion.header>

        {/* Split Screen Layout */}
        <div className="max-w-7xl mx-auto px-6 py-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 h-[calc(100vh-120px)]">

            {/* Left Side - System Status & Modifications */}
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5 }}
              className="space-y-6 overflow-y-auto pr-3"
              style={{ scrollbarWidth: 'thin' }}
            >
              <div className="sticky top-0 z-10 glass-effect rounded-xl p-4 mb-4">
                <h2 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
                  <svg className="w-5 h-5 text-primary-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v4a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
                  </svg>
                  System Status & Configuration
                </h2>
                <p className="text-sm text-gray-600 mt-1">
                  Real-time monitoring and system modifications
                </p>
              </div>

              {/* Project Selector */}
              <ProjectSelector
                currentProjectId={currentProjectId}
                onProjectChange={handleProjectChange}
                projectName={project?.name}
              />

              {/* AI Features */}
              <AIFeatures
                projectId={currentProjectId}
                projectName={project?.name}
              />

              <SystemOverview
                health={health}
                events={events}
                project={project}
                isLoading={loading}
              />
            </motion.div>

            {/* Right Side - Real-time Activity Feed */}
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
              className="overflow-hidden"
            >
              <div className="h-full flex flex-col">
                <div className="glass-effect rounded-xl p-4 mb-4">
                  <h2 className="text-lg font-semibold text-gray-900 flex items-center gap-2">
                    <svg className="w-5 h-5 text-primary-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 10V3L4 14h7v7l9-11h-7z" />
                    </svg>
                    Real-time Activity
                  </h2>
                  <p className="text-sm text-gray-600 mt-1">
                    Live events, AI analysis, and system changes for Project #{currentProjectId}
                  </p>
                </div>

                <div className="flex-1 overflow-hidden">
                  <ActivityFeed
                    activities={[]}
                    isLoading={loading}
                  />
                </div>
              </div>
            </motion.div>
          </div>
        </div>

        {/* Footer */}
        <motion.footer
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 1 }}
          className="glass-effect border-t border-white/20 mt-auto"
        >
          <div className="max-w-7xl mx-auto px-6 py-4">
            <div className="flex items-center justify-between text-sm text-gray-500">
              <div className="flex items-center gap-4">
                <span>GitAIOps Platform v1.0.0</span>
                <span>•</span>
                <span>Powered by Claude Sonnet 4</span>
              </div>
              <div className="flex items-center gap-4">
                <a href="http://localhost:8000/docs" target="_blank" rel="noopener noreferrer" 
                   className="hover:text-primary-600 transition-colors">
                  API Docs
                </a>
                <span>•</span>
                <a href="http://localhost:8000/health/" target="_blank" rel="noopener noreferrer"
                   className="hover:text-primary-600 transition-colors">
                  Health Check
                </a>
              </div>
            </div>
          </div>
        </motion.footer>
      </div>
    </div>
  );
}

export default App;
