import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface AIFeaturesProps {
  projectId: number;
  projectName?: string;
}

interface AIOperation {
  operation_id: string;
  status: string;
  progress: number;
  result?: any;
  error?: string;
  started_at: string;
  completed_at?: string;
}

export const AIFeatures: React.FC<AIFeaturesProps> = ({ projectId, projectName }) => {
  const [activeFeature, setActiveFeature] = useState<string | null>(null);
  const [results, setResults] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [currentOperation, setCurrentOperation] = useState<AIOperation | null>(null);
  const [chatInput, setChatInput] = useState('');
  const [chatHistory, setChatHistory] = useState<Array<{role: string, content: string}>>([]);
  const [showChat, setShowChat] = useState(false);

  const features = [
    {
      id: 'triage',
      name: 'MR Triage',
      description: 'AI-powered merge request analysis',
      icon: '🔍',
      endpoint: '/api/v1/ai/analyze-merge-request',
      requiresInput: true
    },
    {
      id: 'chat',
      name: 'AI Assistant',
      description: 'Conversational AI for project insights',
      icon: '🤖',
      endpoint: '/api/v1/ai/assistant/chat',
      isChat: true
    },
    {
      id: 'security',
      name: 'Security Scan',
      description: 'Vulnerability detection and analysis',
      icon: '🛡️',
      endpoint: '/api/v1/ai/security-scan'
    },
    {
      id: 'performance',
      name: 'Performance Analysis',
      description: 'Pipeline and code performance insights',
      icon: '⚡',
      endpoint: '/api/v1/ai/performance-analysis'
    }
  ];

  // Poll operation status
  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (currentOperation && currentOperation.status === 'running') {
      interval = setInterval(async () => {
        try {
          const response = await fetch(`/api/v1/ai/operations/${currentOperation.operation_id}`);
          if (response.ok) {
            const updatedOperation = await response.json();
            setCurrentOperation(updatedOperation);

            if (updatedOperation.status === 'completed') {
              setResults(updatedOperation.result);
              setLoading(false);
              setCurrentOperation(null);
            } else if (updatedOperation.status === 'failed') {
              setResults({ error: updatedOperation.error });
              setLoading(false);
              setCurrentOperation(null);
            }
          }
        } catch (error) {
          console.error('Failed to poll operation status:', error);
          setLoading(false);
          setCurrentOperation(null);
        }
      }, 2000); // Poll every 2 seconds
    }

    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [currentOperation]);

  const runFeature = async (feature: any) => {
    if (feature.isChat) {
      setShowChat(true);
      setActiveFeature(feature.id);
      return;
    }

    setActiveFeature(feature.id);
    setLoading(true);
    setResults(null);
    setCurrentOperation(null);

    try {
      let requestBody: any = {};

      if (feature.id === 'triage') {
        // For MR triage, we'll use mock data since we don't have real MR info
        requestBody = {
          mr_title: "Update user authentication system",
          mr_description: "Improved security and added 2FA support",
          changed_files: ["src/auth/login.py", "src/auth/models.py", "tests/test_auth.py"],
          diff_content: "Sample diff content for demonstration"
        };
      } else if (feature.id === 'security') {
        requestBody = {
          target_path: ".",
          scan_type: "full",
          include_patterns: ["*.py", "*.js", "*.ts", "*.jsx", "*.tsx"]
        };
      } else if (feature.id === 'performance') {
        requestBody = {
          target_path: ".",
          analysis_type: "full"
        };
      }

      const response = await fetch(feature.endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(requestBody)
      });

      if (response.ok) {
        const data = await response.json();
        if (data.operation_id) {
          // Start polling for operation status
          setCurrentOperation({
            operation_id: data.operation_id,
            status: 'running',
            progress: 0,
            started_at: new Date().toISOString()
          });
        } else {
          // Immediate response (like chat)
          setResults(data);
          setLoading(false);
        }
      } else {
        const errorData = await response.json();
        setResults({ error: errorData.detail || 'Operation failed' });
        setLoading(false);
      }
    } catch (error) {
      console.error('Failed to run AI feature:', error);
      setResults({ error: 'Failed to connect to AI service' });
      setLoading(false);
    }
  };

  const sendChatMessage = async () => {
    if (!chatInput.trim()) return;

    const userMessage = { role: 'user', content: chatInput };
    setChatHistory(prev => [...prev, userMessage]);
    setChatInput('');
    setLoading(true);

    try {
      const response = await fetch('/api/v1/ai/assistant/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          query: chatInput,
          context: `Project ${projectName || projectId}`,
          project_path: "."
        })
      });

      if (response.ok) {
        const data = await response.json();
        const assistantMessage = { role: 'assistant', content: data.response };
        setChatHistory(prev => [...prev, assistantMessage]);
      } else {
        const assistantMessage = { role: 'assistant', content: 'Sorry, I encountered an error. Please try again.' };
        setChatHistory(prev => [...prev, assistantMessage]);
      }
    } catch (error) {
      const assistantMessage = { role: 'assistant', content: 'Failed to connect to AI assistant.' };
      setChatHistory(prev => [...prev, assistantMessage]);
    }

    setLoading(false);
  };

  const renderResults = () => {
    if (!results) return null;

    if (results.error) {
      return (
        <div className="mt-4 p-4 bg-red-500/20 border border-red-500/30 rounded-lg">
          <p className="text-red-300 text-sm">Error: {results.error}</p>
        </div>
      );
    }

    if (activeFeature === 'triage' && results.mr_id) {
      return (
        <div className="mt-4 space-y-3">
          <div className="p-4 bg-gray-800/50 rounded-lg">
            <h4 className="text-white font-medium mb-2">Merge Request Analysis</h4>
            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-400">Risk Level:</span>
                <span className={`font-medium ${
                  results.risk_level === 'high' ? 'text-red-400' :
                  results.risk_level === 'medium' ? 'text-yellow-400' : 'text-green-400'
                }`}>
                  {results.risk_level}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">Auto-mergeable:</span>
                <span className={results.auto_mergeable ? 'text-green-400' : 'text-red-400'}>
                  {results.auto_mergeable ? 'Yes' : 'No'}
                </span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">Confidence:</span>
                <span className="text-white">{(results.confidence_score * 100).toFixed(1)}%</span>
              </div>
            </div>
          </div>

          {results.issues_found?.length > 0 && (
            <div className="p-4 bg-gray-800/50 rounded-lg">
              <h5 className="text-white font-medium mb-2">Issues Found</h5>
              <ul className="space-y-1 text-sm">
                {results.issues_found.slice(0, 3).map((issue: any, idx: number) => (
                  <li key={idx} className="text-gray-300">
                    • {issue.description}
                  </li>
                ))}
              </ul>
            </div>
          )}

          {results.suggestions?.length > 0 && (
            <div className="p-4 bg-gray-800/50 rounded-lg">
              <h5 className="text-white font-medium mb-2">Suggestions</h5>
              <ul className="space-y-1 text-sm">
                {results.suggestions.slice(0, 3).map((suggestion: string, idx: number) => (
                  <li key={idx} className="text-gray-300">
                    • {suggestion}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      );
    }

    if (activeFeature === 'security' && results.scan_id) {
      return (
        <div className="mt-4 space-y-3">
          <div className="p-4 bg-gray-800/50 rounded-lg">
            <h4 className="text-white font-medium mb-2">Security Scan Results</h4>
            <div className="grid grid-cols-2 gap-4 text-sm">
              <div>
                <span className="text-gray-400">Files Scanned:</span>
                <span className="text-white ml-2">{results.files_scanned}</span>
              </div>
              <div>
                <span className="text-gray-400">Duration:</span>
                <span className="text-white ml-2">{results.scan_duration?.toFixed(1)}s</span>
              </div>
            </div>

            <div className="mt-3 grid grid-cols-4 gap-2 text-xs">
              <div className="text-center p-2 bg-red-500/20 rounded">
                <div className="text-red-400 font-bold">{results.risk_summary?.high || 0}</div>
                <div className="text-gray-400">High</div>
              </div>
              <div className="text-center p-2 bg-yellow-500/20 rounded">
                <div className="text-yellow-400 font-bold">{results.risk_summary?.medium || 0}</div>
                <div className="text-gray-400">Medium</div>
              </div>
              <div className="text-center p-2 bg-blue-500/20 rounded">
                <div className="text-blue-400 font-bold">{results.risk_summary?.low || 0}</div>
                <div className="text-gray-400">Low</div>
              </div>
              <div className="text-center p-2 bg-gray-500/20 rounded">
                <div className="text-gray-400 font-bold">{results.risk_summary?.critical || 0}</div>
                <div className="text-gray-400">Critical</div>
              </div>
            </div>
          </div>

          {results.recommendations?.length > 0 && (
            <div className="p-4 bg-gray-800/50 rounded-lg">
              <h5 className="text-white font-medium mb-2">Recommendations</h5>
              <ul className="space-y-1 text-sm">
                {results.recommendations.slice(0, 3).map((rec: string, idx: number) => (
                  <li key={idx} className="text-gray-300">
                    • {rec}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      );
    }

    if (activeFeature === 'performance' && results.analysis_id) {
      return (
        <div className="mt-4 space-y-3">
          <div className="p-4 bg-gray-800/50 rounded-lg">
            <h4 className="text-white font-medium mb-2">Performance Analysis</h4>
            <div className="flex justify-between items-center mb-3">
              <span className="text-gray-400">Performance Score:</span>
              <div className="flex items-center">
                <div className="w-24 h-2 bg-gray-700 rounded-full mr-2">
                  <div 
                    className="h-2 bg-gradient-to-r from-red-500 via-yellow-500 to-green-500 rounded-full"
                    style={{ width: `${results.performance_score * 100}%` }}
                  />
                </div>
                <span className="text-white text-sm">{(results.performance_score * 100).toFixed(1)}%</span>
              </div>
            </div>

            <div className="text-sm space-y-1">
              <div className="flex justify-between">
                <span className="text-gray-400">Files Analyzed:</span>
                <span className="text-white">{results.metrics?.total_files_analyzed || 0}</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-400">Issues Found:</span>
                <span className="text-white">{results.metrics?.performance_issues_found || 0}</span>
              </div>
            </div>
          </div>

          {results.optimization_suggestions?.length > 0 && (
            <div className="p-4 bg-gray-800/50 rounded-lg">
              <h5 className="text-white font-medium mb-2">Optimization Suggestions</h5>
              <ul className="space-y-1 text-sm">
                {results.optimization_suggestions.slice(0, 3).map((suggestion: string, idx: number) => (
                  <li key={idx} className="text-gray-300">
                    • {suggestion}
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      );
    }

    return (
      <div className="mt-4 p-4 bg-gray-800/50 rounded-lg">
        <pre className="text-gray-300 text-xs overflow-auto">
          {JSON.stringify(results, null, 2)}
        </pre>
      </div>
    );
  };

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h3 className="text-lg font-semibold text-dark-primary">
          AI Features
        </h3>
        <span className="text-sm text-dark-muted">
          Project #{projectId}
        </span>
      </div>

      <div className="grid grid-cols-2 gap-3">
        {features.map((feature) => (
          <motion.button
            key={feature.id}
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={() => runFeature(feature)}
            disabled={loading}
            className={`p-4 rounded-lg border transition-all duration-300 text-left ${
              activeFeature === feature.id
                ? 'border-blue-500 bg-blue-500/10'
                : 'border-gray-600 bg-gray-800/30 hover:border-gray-500'
            } ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}
          >
            <div className="flex items-start space-x-3">
              <span className="text-2xl">{feature.icon}</span>
              <div className="flex-1 min-w-0">
                <h4 className="font-medium text-white text-sm">
                  {feature.name}
                </h4>
                <p className="text-xs text-gray-400 mt-1 leading-relaxed">
                  {feature.description}
                </p>
              </div>
            </div>
          </motion.button>
        ))}
      </div>

      {/* Progress indicator */}
      {loading && currentOperation && (
        <div className="mt-4 p-4 bg-gray-800/50 rounded-lg">
          <div className="flex items-center justify-between mb-2">
            <span className="text-white text-sm">Processing...</span>
            <span className="text-gray-400 text-xs">{currentOperation.status}</span>
          </div>
          <div className="w-full bg-gray-700 rounded-full h-2">
            <div 
              className="bg-blue-500 h-2 rounded-full transition-all duration-500"
              style={{ width: `${currentOperation.progress * 100}%` }}
            />
          </div>
        </div>
      )}

      {/* Results display */}
      {renderResults()}

      {/* Chat Modal */}
      <AnimatePresence>
        {showChat && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/50 backdrop-blur-sm z-[9999] flex items-center justify-center p-4"
            onClick={() => setShowChat(false)}
          >
            <motion.div
              initial={{ scale: 0.9, y: 20 }}
              animate={{ scale: 1, y: 0 }}
              exit={{ scale: 0.9, y: 20 }}
              onClick={(e) => e.stopPropagation()}
              className="bg-gray-900 rounded-xl border border-gray-700 w-full max-w-2xl h-[600px] flex flex-col"
            >
              {/* Chat Header */}
              <div className="flex items-center justify-between p-4 border-b border-gray-700">
                <div className="flex items-center space-x-3">
                  <span className="text-2xl">🤖</span>
                  <h3 className="text-lg font-semibold text-white">AI Assistant</h3>
                </div>
                <button
                  onClick={() => setShowChat(false)}
                  className="text-gray-400 hover:text-white transition-colors"
                >
                  ✕
                </button>
              </div>

              {/* Chat Messages */}
              <div className="flex-1 overflow-y-auto p-4 space-y-4">
                {chatHistory.length === 0 && (
                  <div className="text-center text-gray-400 mt-8">
                    <p>Ask me anything about your project!</p>
                    <p className="text-sm mt-2">Try: "What files should I review?" or "Help me optimize performance"</p>
                  </div>
                )}

                {chatHistory.map((message, index) => (
                  <div
                    key={index}
                    className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
                  >
                    <div
                      className={`max-w-[80%] p-3 rounded-lg ${
                        message.role === 'user'
                          ? 'bg-blue-600 text-white'
                          : 'bg-gray-800 text-gray-100'
                      }`}
                    >
                      <p className="text-sm">{message.content}</p>
                    </div>
                  </div>
                ))}

                {loading && (
                  <div className="flex justify-start">
                    <div className="bg-gray-800 text-gray-100 p-3 rounded-lg">
                      <div className="flex items-center space-x-2">
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" />
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }} />
                        <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }} />
                      </div>
                    </div>
                  </div>
                )}
              </div>

              {/* Chat Input */}
              <div className="p-4 border-t border-gray-700">
                <div className="flex space-x-2">
                  <input
                    type="text"
                    value={chatInput}
                    onChange={(e) => setChatInput(e.target.value)}
                    onKeyPress={(e) => e.key === 'Enter' && !loading && sendChatMessage()}
                    placeholder="Ask the AI assistant..."
                    className="flex-1 bg-gray-800 text-white px-4 py-2 rounded-lg border border-gray-600 focus:border-blue-500 focus:outline-none"
                    disabled={loading}
                  />
                  <button
                    onClick={sendChatMessage}
                    disabled={loading || !chatInput.trim()}
                    className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                  >
                    Send
                  </button>
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};
