import React, { useState } from 'react';
import { motion } from 'framer-motion';

interface ProjectSelectorProps {
  currentProjectId: number;
  onProjectChange: (projectId: number) => void;
  projectName?: string;
}

export const ProjectSelector: React.FC<ProjectSelectorProps> = ({
  currentProjectId,
  onProjectChange,
  projectName
}) => {
  const [inputValue, setInputValue] = useState(currentProjectId.toString());
  const [isEditing, setIsEditing] = useState(false);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const newProjectId = parseInt(inputValue);
    if (!isNaN(newProjectId) && newProjectId > 0) {
      onProjectChange(newProjectId);
      setIsEditing(false);
    }
  };

  const handleCancel = () => {
    setInputValue(currentProjectId.toString());
    setIsEditing(false);
  };

  return (
    <div className="space-y-4">
      {/* Current Project Display */}
      <div className="glass-dark rounded-xl p-4 border border-white/5">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-br from-blue-500/20 to-purple-500/20 rounded-lg flex items-center justify-center border border-blue-500/20">
              <svg className="w-5 h-5 text-blue-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 11H5m14 0a2 2 0 012 2v6a2 2 0 01-2 2H5a2 2 0 01-2-2v-6a2 2 0 012-2m14 0V9a2 2 0 00-2-2M5 9a2 2 0 00-2 2v2a2 2 0 002 2m0 0h14m-14 0a2 2 0 002 2v2a2 2 0 01-2 2M5 9V7a2 2 0 012-2h12a2 2 0 012 2v2M7 13h10v-2H7v2z" />
              </svg>
            </div>
            <div>
              <div className="text-white font-medium mono">
                Project #{currentProjectId}
              </div>
              <div className="text-gray-400 text-sm">
                {projectName || 'Loading project details...'}
              </div>
            </div>
          </div>

          <button
            onClick={() => setIsEditing(true)}
            className="px-3 py-1.5 bg-blue-500/20 hover:bg-blue-500/30 text-blue-400 rounded-lg transition-all duration-200 text-sm font-medium border border-blue-500/20 hover:border-blue-500/40"
          >
            Change
          </button>
        </div>
      </div>

      {/* Edit Form */}
      {isEditing && (
        <motion.div
          initial={{ opacity: 0, height: 0 }}
          animate={{ opacity: 1, height: 'auto' }}
          exit={{ opacity: 0, height: 0 }}
          className="glass-dark rounded-xl p-4 border border-purple-500/20"
        >
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-300 mb-2">
                Enter New Project ID
              </label>
              <input
                type="number"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                className="w-full px-4 py-2 bg-white/5 border border-white/10 rounded-lg text-white placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500/50 transition-all mono"
                placeholder="Enter project ID..."
                autoFocus
                min="1"
              />
            </div>

            <div className="flex gap-2">
              <button
                type="submit"
                disabled={!inputValue || parseInt(inputValue) <= 0}
                className="flex-1 px-4 py-2 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-lg hover:from-blue-600 hover:to-purple-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 font-medium"
              >
                Switch Project
              </button>
              <button
                type="button"
                onClick={handleCancel}
                className="px-4 py-2 bg-white/5 hover:bg-white/10 text-gray-300 rounded-lg transition-all duration-200 border border-white/10"
              >
                Cancel
              </button>
            </div>
          </form>
        </motion.div>
      )}

      {/* Quick Actions */}
      <div className="grid grid-cols-2 gap-2">
        <button
          onClick={() => {
            setInputValue('278964');
            onProjectChange(278964);
          }}
          className="p-3 glass-dark rounded-lg hover:bg-white/10 transition-all duration-200 text-left border border-white/5 hover:border-white/20"
        >
          <div className="text-gray-300 text-sm font-medium">GitLab Demo</div>
          <div className="text-gray-500 text-xs mono">#278964</div>
        </button>

        <button
          onClick={() => {
            setInputValue(currentProjectId.toString());
            onProjectChange(currentProjectId);
          }}
          className="p-3 glass-dark rounded-lg hover:bg-white/10 transition-all duration-200 text-left border border-white/5 hover:border-white/20"
        >
          <div className="text-gray-300 text-sm font-medium">Refresh</div>
          <div className="text-gray-500 text-xs">Reload current</div>
        </button>
      </div>
    </div>
  );
};
