import React, { useState, useEffect, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  Terminal, 
  FileText, 
  Folder, 
  GitBranch, 
  Play, 
  MessageSquare,
  Search,
  Database,
  Settings,
  Activity,
  Zap,
  Code,
  TestTube,
  Brain,
  Upload,
  Download,
  Cpu,
  MonitorSpeaker,
  CheckCircle,
  XCircle,
  Clock,
  AlertCircle
} from 'lucide-react';

// Types
interface MCPResource {
  uri: string;
  name: string;
  description: string;
  mime_type: string;
}

interface MCPTool {
  name: string;
  description: string;
  input_schema: any;
}

interface CommandResult {
  command: string;
  exit_code: number;
  stdout: string;
  stderr: string;
  execution_time: number;
  timestamp: string;
}

interface FileContent {
  path: string;
  content: string;
  language: string;
  size: number;
  lines: number;
}

interface LLMResponse {
  response: string;
  model: string;
  tokens_used?: number;
  timestamp: string;
}

const EnhancedMCPInterface: React.FC = () => {
  const [activeTab, setActiveTab] = useState<'files' | 'terminal' | 'llm' | 'tests' | 'git'>('files');
  const [mcpStatus, setMcpStatus] = useState<'connecting' | 'connected' | 'error'>('connecting');
  const [availableTools, setAvailableTools] = useState<MCPTool[]>([]);
  const [availableResources, setAvailableResources] = useState<MCPResource[]>([]);

  // State for different operations
  const [terminalHistory, setTerminalHistory] = useState<CommandResult[]>([]);
  const [currentDirectory, setCurrentDirectory] = useState<string>('.');
  const [fileContent, setFileContent] = useState<FileContent | null>(null);
  const [llmConversation, setLLMConversation] = useState<Array<{role: string, content: string}>>([]);
  const [projectFiles, setProjectFiles] = useState<any[]>([]);
  const [gitInfo, setGitInfo] = useState<any>(null);

  // UI state
  const [commandInput, setCommandInput] = useState<string>('');
  const [llmInput, setLLMInput] = useState<string>('');
  const [selectedFile, setSelectedFile] = useState<string>('');
  const [isLoading, setIsLoading] = useState<boolean>(false);

  // MCP API base URL
  const MCP_BASE = 'http://localhost:8001';
  const API_BASE = 'http://localhost:8000/api/v1/mcp';

  // Initialize MCP connection
  useEffect(() => {
    initializeMCP();
  }, []);

  const initializeMCP = async () => {
    try {
      // Check MCP server status
      const statusResponse = await fetch(`${API_BASE}/status`);
      if (statusResponse.ok) {
        setMcpStatus('connected');

        // Load available tools and resources
        await loadMCPCapabilities();
        await loadInitialData();
      } else {
        setMcpStatus('error');
      }
    } catch (error) {
      console.error('Failed to initialize MCP:', error);
      setMcpStatus('error');
    }
  };

  const loadMCPCapabilities = async () => {
    try {
      // Load available tools
      const toolsResponse = await fetch(`${API_BASE}/tools`);
      if (toolsResponse.ok) {
        const toolsData = await toolsResponse.json();
        setAvailableTools(toolsData.tools || []);
      }

      // Load available resources
      const resourcesResponse = await fetch(`${API_BASE}/resources`);
      if (resourcesResponse.ok) {
        const resourcesData = await resourcesResponse.json();
        setAvailableResources(resourcesData.resources || []);
      }
    } catch (error) {
      console.error('Failed to load MCP capabilities:', error);
    }
  };

  const loadInitialData = async () => {
    try {
      // Load project files
      await executeCommand('list_project_files', { max_depth: 2 });

      // Load git info
      await executeCommand('get_repository_info', {});

      // Load project summary
      await executeCommand('create_project_summary', {});
    } catch (error) {
      console.error('Failed to load initial data:', error);
    }
  };

  const executeCommand = async (toolName: string, toolArgs: any) => {
    setIsLoading(true);
    try {
      const response = await fetch(`${API_BASE}/tools/call`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name: toolName, arguments: toolArgs })
      });

      if (response.ok) {
        const result = await response.json();
        return JSON.parse(result.result);
      } else {
        throw new Error(`Command failed: ${response.statusText}`);
      }
    } catch (error) {
      console.error(`Failed to execute ${toolName}:`, error);
      throw error;
    } finally {
      setIsLoading(false);
    }
  };

  const executeBashCommand = async () => {
    if (!commandInput.trim()) return;

    try {
      const result = await executeCommand('execute_bash_command', {
        command: commandInput,
        working_directory: currentDirectory,
        timeout: 30
      });

      setTerminalHistory(prev => [...prev, result]);
      setCommandInput('');
    } catch (error) {
      console.error('Command execution failed:', error);
    }
  };

  const readFile = async (filePath: string) => {
    try {
      const result = await executeCommand('read_file_content', { file_path: filePath });
      setFileContent(result);
      setSelectedFile(filePath);
    } catch (error) {
      console.error('Failed to read file:', error);
    }
  };

  const chatWithLLM = async () => {
    if (!llmInput.trim()) return;

    const userMessage = { role: 'user', content: llmInput };
    setLLMConversation(prev => [...prev, userMessage]);
    setLLMInput('');

    try {
      const result = await executeCommand('chat_with_llm', {
        message: llmInput,
        model: 'gpt-4',
        context: `GitAIOps platform - working directory: ${currentDirectory}`
      });

      const assistantMessage = { role: 'assistant', content: result.response };
      setLLMConversation(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('LLM chat failed:', error);
      const errorMessage = { role: 'assistant', content: 'Sorry, I encountered an error. Please try again.' };
      setLLMConversation(prev => [...prev, errorMessage]);
    }
  };

  const getStatusIcon = () => {
    switch (mcpStatus) {
      case 'connected': return <CheckCircle className="w-4 h-4 text-green-400" />;
      case 'error': return <XCircle className="w-4 h-4 text-red-400" />;
      default: return <Clock className="w-4 h-4 text-yellow-400" />;
    }
  };

  const getStatusText = () => {
    switch (mcpStatus) {
      case 'connected': return 'Connected to EnrichMCP';
      case 'error': return 'Connection Failed';
      default: return 'Connecting...';
    }
  };

  const TabButton: React.FC<{
    id: string;
    label: string;
    icon: React.ReactNode;
    isActive: boolean;
    onClick: () => void;
  }> = ({ id, label, icon, isActive, onClick }) => (
    <motion.button
      whileHover={{ scale: 1.05 }}
      whileTap={{ scale: 0.95 }}
      onClick={onClick}
      className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-all ${
        isActive
          ? 'bg-blue-500 text-white shadow-lg'
          : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
      }`}
    >
      {icon}
      <span className="font-medium">{label}</span>
    </motion.button>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 text-white p-6">
      {/* Header */}
      <div className="flex justify-between items-center mb-8">
        <div className="flex items-center space-x-4">
          <Brain className="w-8 h-8 text-purple-400" />
          <div>
            <h1 className="text-3xl font-bold bg-gradient-to-r from-purple-400 to-blue-400 bg-clip-text text-transparent">
              Enhanced GitAIOps Platform
            </h1>
            <p className="text-gray-400">AI-Powered DevOps with EnrichMCP Integration</p>
          </div>
        </div>

        <div className="flex items-center space-x-3">
          {getStatusIcon()}
          <span className="text-sm">{getStatusText()}</span>
          <div className="px-3 py-1 bg-gray-700 rounded-full text-xs">
            {availableTools.length} Tools • {availableResources.length} Resources
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="flex space-x-4 mb-8">
        <TabButton
          id="files"
          label="Files & Projects"
          icon={<Folder className="w-5 h-5" />}
          isActive={activeTab === 'files'}
          onClick={() => setActiveTab('files')}
        />
        <TabButton
          id="terminal"
          label="Terminal & Commands"
          icon={<Terminal className="w-5 h-5" />}
          isActive={activeTab === 'terminal'}
          onClick={() => setActiveTab('terminal')}
        />
        <TabButton
          id="llm"
          label="AI Assistant"
          icon={<MessageSquare className="w-5 h-5" />}
          isActive={activeTab === 'llm'}
          onClick={() => setActiveTab('llm')}
        />
        <TabButton
          id="tests"
          label="Testing"
          icon={<TestTube className="w-5 h-5" />}
          isActive={activeTab === 'tests'}
          onClick={() => setActiveTab('tests')}
        />
        <TabButton
          id="git"
          label="Git Operations"
          icon={<GitBranch className="w-5 h-5" />}
          isActive={activeTab === 'git'}
          onClick={() => setActiveTab('git')}
        />
      </div>

      {/* Content Areas */}
      <AnimatePresence mode="wait">
        {activeTab === 'files' && (
          <motion.div
            key="files"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="grid grid-cols-1 lg:grid-cols-2 gap-6"
          >
            {/* File Explorer */}
            <div className="bg-gray-800/50 backdrop-blur-lg p-6 rounded-xl border border-gray-700">
              <h3 className="text-lg font-semibold mb-4 flex items-center space-x-2">
                <Folder className="w-5 h-5 text-blue-400" />
                <span>Project Files</span>
              </h3>

              <div className="space-y-2 max-h-96 overflow-y-auto">
                {projectFiles.map((file, index) => (
                  <motion.div
                    key={index}
                    whileHover={{ scale: 1.02 }}
                    onClick={() => readFile(file.path)}
                    className="flex items-center space-x-3 p-3 bg-gray-700/30 rounded-lg cursor-pointer hover:bg-gray-700/50 transition-all"
                  >
                    <FileText className="w-4 h-4 text-gray-400" />
                    <div className="flex-1">
                      <p className="text-sm font-medium">{file.name}</p>
                      <p className="text-xs text-gray-400">{file.path}</p>
                    </div>
                    <span className="text-xs text-gray-500">{file.size}B</span>
                  </motion.div>
                ))}
              </div>
            </div>

            {/* File Content Viewer */}
            <div className="bg-gray-800/50 backdrop-blur-lg p-6 rounded-xl border border-gray-700">
              <h3 className="text-lg font-semibold mb-4 flex items-center space-x-2">
                <Code className="w-5 h-5 text-green-400" />
                <span>File Content</span>
                {selectedFile && <span className="text-sm text-gray-400">({selectedFile})</span>}
              </h3>

              {fileContent ? (
                <div className="space-y-4">
                  <div className="flex items-center justify-between text-sm text-gray-400">
                    <span>{fileContent.language}</span>
                    <span>{fileContent.lines} lines • {fileContent.size} bytes</span>
                  </div>

                  <div className="bg-gray-900/50 p-4 rounded-lg overflow-x-auto">
                    <pre className="text-sm">
                      <code>{fileContent.content}</code>
                    </pre>
                  </div>
                </div>
              ) : (
                <div className="flex items-center justify-center h-64 text-gray-400">
                  <div className="text-center">
                    <FileText className="w-12 h-12 mx-auto mb-4 opacity-50" />
                    <p>Select a file to view its content</p>
                  </div>
                </div>
              )}
            </div>
          </motion.div>
        )}

        {activeTab === 'terminal' && (
          <motion.div
            key="terminal"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="space-y-6"
          >
            {/* Command Input */}
            <div className="bg-gray-800/50 backdrop-blur-lg p-6 rounded-xl border border-gray-700">
              <h3 className="text-lg font-semibold mb-4 flex items-center space-x-2">
                <Terminal className="w-5 h-5 text-green-400" />
                <span>Command Execution</span>
                <span className="text-sm text-gray-400">({currentDirectory})</span>
              </h3>

              <div className="flex space-x-4">
                <input
                  type="text"
                  value={commandInput}
                  onChange={(e) => setCommandInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && executeBashCommand()}
                  placeholder="Enter bash command..."
                  className="flex-1 bg-gray-700 text-white px-4 py-2 rounded-lg border border-gray-600 focus:border-blue-500 focus:outline-none"
                />
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={executeBashCommand}
                  disabled={isLoading}
                  className="px-6 py-2 bg-gradient-to-r from-green-500 to-blue-500 rounded-lg font-semibold disabled:opacity-50"
                >
                  <Play className="w-4 h-4" />
                </motion.button>
              </div>
            </div>

            {/* Command History */}
            <div className="bg-gray-800/50 backdrop-blur-lg p-6 rounded-xl border border-gray-700">
              <h3 className="text-lg font-semibold mb-4 flex items-center space-x-2">
                <Activity className="w-5 h-5 text-blue-400" />
                <span>Command History</span>
              </h3>

              <div className="space-y-4 max-h-96 overflow-y-auto">
                {terminalHistory.map((cmd, index) => (
                  <div key={index} className="bg-gray-900/50 p-4 rounded-lg">
                    <div className="flex items-center justify-between mb-2">
                      <code className="text-green-400">$ {cmd.command}</code>
                      <div className="flex items-center space-x-2">
                        <span className={`px-2 py-1 rounded text-xs ${
                          cmd.exit_code === 0 ? 'bg-green-500/20 text-green-400' : 'bg-red-500/20 text-red-400'
                        }`}>
                          Exit: {cmd.exit_code}
                        </span>
                        <span className="text-xs text-gray-400">{cmd.execution_time.toFixed(2)}s</span>
                      </div>
                    </div>

                    {cmd.stdout && (
                      <div className="mt-2">
                        <p className="text-xs text-gray-400 mb-1">Output:</p>
                        <pre className="text-sm text-gray-300 whitespace-pre-wrap">{cmd.stdout}</pre>
                      </div>
                    )}

                    {cmd.stderr && (
                      <div className="mt-2">
                        <p className="text-xs text-red-400 mb-1">Error:</p>
                        <pre className="text-sm text-red-300 whitespace-pre-wrap">{cmd.stderr}</pre>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </div>
          </motion.div>
        )}

        {activeTab === 'llm' && (
          <motion.div
            key="llm"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="space-y-6"
          >
            {/* Chat Interface */}
            <div className="bg-gray-800/50 backdrop-blur-lg p-6 rounded-xl border border-gray-700">
              <h3 className="text-lg font-semibold mb-4 flex items-center space-x-2">
                <Brain className="w-5 h-5 text-purple-400" />
                <span>AI Assistant</span>
              </h3>

              {/* Conversation */}
              <div className="space-y-4 max-h-96 overflow-y-auto mb-4">
                {llmConversation.map((message, index) => (
                  <div key={index} className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                    <div className={`max-w-3xl p-4 rounded-lg ${
                      message.role === 'user' 
                        ? 'bg-blue-500/20 text-blue-100' 
                        : 'bg-gray-700/50 text-gray-100'
                    }`}>
                      <p className="text-sm font-medium mb-1 capitalize">{message.role}</p>
                      <p className="whitespace-pre-wrap">{message.content}</p>
                    </div>
                  </div>
                ))}
              </div>

              {/* Input */}
              <div className="flex space-x-4">
                <input
                  type="text"
                  value={llmInput}
                  onChange={(e) => setLLMInput(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && chatWithLLM()}
                  placeholder="Ask the AI assistant anything about your project..."
                  className="flex-1 bg-gray-700 text-white px-4 py-2 rounded-lg border border-gray-600 focus:border-purple-500 focus:outline-none"
                />
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={chatWithLLM}
                  disabled={isLoading}
                  className="px-6 py-2 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg font-semibold disabled:opacity-50"
                >
                  <MessageSquare className="w-4 h-4" />
                </motion.button>
              </div>
            </div>
          </motion.div>
        )}

        {activeTab === 'tests' && (
          <motion.div
            key="tests"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="text-center py-12"
          >
            <TestTube className="w-16 h-16 mx-auto mb-4 text-green-400 opacity-50" />
            <h3 className="text-xl font-semibold mb-2">Real-time Testing</h3>
            <p className="text-gray-400">Testing interface integration coming soon...</p>
          </motion.div>
        )}

        {activeTab === 'git' && (
          <motion.div
            key="git"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="text-center py-12"
          >
            <GitBranch className="w-16 h-16 mx-auto mb-4 text-blue-400 opacity-50" />
            <h3 className="text-xl font-semibold mb-2">Git Operations</h3>
            <p className="text-gray-400">Git interface integration coming soon...</p>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Loading Overlay */}
      {isLoading && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center"
        >
          <div className="bg-gray-800 p-6 rounded-xl border border-gray-700">
            <div className="flex items-center space-x-3">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-400"></div>
              <span>Processing...</span>
            </div>
          </div>
        </motion.div>
      )}
    </div>
  );
};

export default EnhancedMCPInterface;
